import React from 'react';
import equipmentStateHistory from '../../data/equipmentStateHistory.json';
import { Container } from 'react-bootstrap';

function EquipmentStatus() {
    // função para exibir o histórico de estados dos equipamentos
    function showEquipmentHistory() {
      return equipmentStateHistory.map((equipment) => {
        return (
          <div key={equipment.equipmentId}>
            <h3>Equipamento: {equipment.equipmentId}</h3>
            <ul>
              {equipment.states.map((state) => {
                return (
                  <li key={state.equipmentStateId}>
                    Data: {state.date}, Estado: {state.equipmentStateId}
                  </li>
                );
              })}
            </ul>
          </div>
        );
      });
    }
  
    return (
      <div>
        <h1>Histórico de estados dos equipamentos</h1>
        {showEquipmentHistory()}
      </div>
    );
  }

export default EquipmentStatus;
