import React, { useState } from 'react';
import equipmentPositionHistory from '../../data/equipmentPositionHistory.json';
import equipmentStateHistory from '../../data/equipmentStateHistory.json';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Container } from 'react-bootstrap';
import 'leaflet/dist/leaflet.css';

function Map() {
    const lastPositions = {};
    const lastStates = {};

    const teste = [51.505, -0.09]

    const customIcon = new Icon({
        iconUrl: '/marker.png',
        iconSize: [15, 25]
    })

    //Pesquisa dentro do Json onde se localiza o equipamento com a data mais recente de sua localização e retorna sua localização
    equipmentPositionHistory.forEach(equipment => {
        const lastPosition = equipment.positions.reduce((prev, current) => {
            return (new Date(prev.date) > new Date(current.date)) ? prev : current;
        });
        lastPositions[equipment.equipmentId] = lastPosition
    });

    const markers = Object.values(lastPositions).map((position) => (
        <Marker key={`${position.lat}-${position.lon}`} position={[position.lat, position.lon]} icon={customIcon}>
          <Popup>
            {`Lat: ${position.lat}, Lon: ${position.lon}`}
          </Popup>
        </Marker>
    ));

    //Pesquisa dentro do Json qual o estado atual de cada equipamente
    equipmentStateHistory.forEach(equipment => {
        const lastState = equipment.states.reduce((prev, current) => {
            return (new Date(prev.date) > new Date(current.date)) ? prev : current;
        });
        lastStates[equipment.equipmentId] = lastState

        let stateText = '';
        if (lastState.equipmentStateId === "0808344c-454b-4c36-89e8-d7687e692d57") {
            const stateText = "Operando"
        } else if (lastState.equipmentStateId === "baff9783-84e8-4e01-874b-6fd743b875ad") {
            const stateText = "Parado"
        } else if (lastState.equipmentStateId === "03b2d446-e3ba-4c82-8dc2-a5611fea6e1f") {
            const stateText = "Manutenção"
        }

    });

    // Itera sobre cada elemento do array lastStates
lastStates.forEach((state, equipmentId) => {
    // Localiza o marcador correspondente com o mesmo equipmentId
    const marker = markers.find(marker => marker.options.equipmentId === equipmentId);
    
    // Atualiza o conteúdo do popup com as informações de estado
    if (marker) {
      const content = `
        <div>
          <strong>Equipment ID:</strong> ${equipmentId}
          <br />
          <strong>Latitude:</strong> ${marker.getLatLng().lat}
          <br />
          <strong>Longitude:</strong> ${marker.getLatLng().lng}
          <br />
          <strong>State:</strong> ${state.equipmentStateId === 1 ? 'Operando' : state.equipmentStateId === 2 ? 'Parado' : 'Manutenção'}
        </div>
      `;
      marker.getPopup().setContent(content);
    }
  });

    console.log(lastStates)

    const initialPosition = markers.length ? markers[0].props.position : [-23.55, -46.64];
        
    return(
    <Container>
    <section className='map-component'>
        <div className='map'>
        <MapContainer center={initialPosition} zoom={10} scrollWheelZoom={true}>
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            /> 
            {markers}
        </MapContainer>
        </div>
    </section>
    <div>
    {Object.keys(lastPositions).map(equipmentId => {
      const position = lastPositions[equipmentId];
      return (
        <div key={equipmentId}>
          <p>Equipment ID: {equipmentId}</p>
          <p>Last position: {position.lat}, {position.lon}</p>
        </div>
      );
    })}
  </div>
    </Container>
    )
}

export default Map;
