import React, { useState, useEffect } from 'react';
import equipmentStateHistory from '../../data/equipmentStateHistory.json';
import { Button, Card, Col, Row } from 'react-bootstrap';

function EquipmentStatus(props) {
  const [selectedEquipmentHistory, setSelectedEquipmentHistory] = useState([]);
  const [equipmentStateHistoryData, setEquipmentStateHistoryData] = useState([]);

  useEffect(() => {
    if (props.selectedEquipmentId === null) {
      setSelectedEquipmentHistory([]);
      return;
    }

    const filteredEquipment = equipmentStateHistory.filter(
      (equipment) => equipment.equipmentId === props.selectedEquipmentId
    );

    if (filteredEquipment.length > 0) {
      setSelectedEquipmentHistory(filteredEquipment[0].states);
      setEquipmentStateHistoryData(filteredEquipment[0].states);
    } else {
      setSelectedEquipmentHistory([]);
      setEquipmentStateHistoryData([]);
    }
  }, [props.selectedEquipmentId]);

  // função para exibir o histórico de estados dos equipamentos
  function renderEquipmentHistory() {
    if (equipmentStateHistoryData.length > 0) {
      return equipmentStateHistoryData.map((state) => {
        return (
          <Row key={state.equipmentStateId}>
            <Col>
              <Card>
                <Card.Body>
                  <Card.Title>Data: {state.date}</Card.Title>
                  <Card.Text className="">Estado: {state.equipmentStateId}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        );
      });
    } else {
      return (
        <h2 className="text-center">
          Selecione um equipamento para ver seu histórico de estados.
        </h2>
      );
    }
  }

  return (
    <div>
      {props.selectedEquipmentId !== null && (
        <>
          <h1>Histórico de estados dos equipamentos</h1>
          <Button
            className="mb-3"
            variant="danger"
            onClick={() => props.showStatusHistory(null)}
          >
            Fechar <i class="fas fa-times"></i>
          </Button>
        </>
      )}
      {renderEquipmentHistory()}
    </div>
  );
}

export default EquipmentStatus;
