import React from 'react';
import equipmentStateHistory from '../../data/equipmentStateHistory.json';
import { Container } from 'react-bootstrap';

function EquipmentStatus(props) {
    console.log(props.selectedEquipmentId)
    // função para exibir o histórico de estados dos equipamentos
    function showEquipmentHistory() {
      const filteredEquipment = equipmentStateHistory.filter(
        (equipment) => equipment.equipmentId === props.selectedEquipmentId
      );

      console.log(filteredEquipment)

      if(filteredEquipment.lengh > 0) {
        return filteredEquipment[0].states.map((state) => {
            return (
                <li key={state.equipmentStateId}>
                    Data: {state.date}, Estado: {state.equipmentStateId}
                </li>
            )
        })
      } else {
        <p>Selecione um equipamento para ver seu históricp de estados.</p>
      }
    }
  
    return (
      <Container>
        <div>
            <h1>Histórico de estados dos equipamentos</h1>
            <ul>{showEquipmentHistory()}</ul>
        </div>
      </Container>
    );
  }

export default EquipmentStatus;
