import React, { useState } from 'react';
import equipmentPositionHistory from '../../data/equipmentPositionHistory.json';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Container } from 'react-bootstrap';
import 'leaflet/dist/leaflet.css';

function Map() {
    const [positions, setPositions] = useState([]);

    const position = [51.505, -0.09]

    const customIcon = new Icon({
        iconUrl: '/marker.png',
        iconSize: [15, 25]
    })

    const obj = JSON.parse(equipmentPositionHistory);

    const mostRecentPosition = obj.reduce((acc, current) => {
        const mostRecentDate = current.positions.reduce((acc, current) => {
          if (new Date(current.date) > new Date(acc.date)) {
            return current;
          } else {
            return acc;
          }
        });
        
        if (!acc.date || new Date(mostRecentDate.date) > new Date(acc.date)) {
          return { equipmentId: current.equipmentId, date: mostRecentDate.date };
        } else {
          return acc;
        }
      }, {});

    console.log(mostRecentPosition)
        
    return(
    <Container>
    <section className='map-component'>
        <div className='map'>
        <MapContainer center={position} zoom={13} scrollWheelZoom={true}>
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={position} icon={customIcon}>
                <Popup className="custom-popup">
                    A pretty CSS3 popup. <br /> Easily customizable.
                </Popup>
            </Marker>
        </MapContainer>
        </div>
    </section>
    </Container>
    )
}

export default Map;
