import React, { useState } from 'react';
import equipmentPositionHistory from '../../data/equipmentPositionHistory.json';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Container } from 'react-bootstrap';
import 'leaflet/dist/leaflet.css';

function Map() {
    const [positions, setPositions] = useState([]);

    const position = [51.505, -0.09]

    const customIcon = new Icon({
        iconUrl: '/marker.png',
        iconSize: [15, 25]
    })

    const obj = JSON.parse(equipmentPositionHistory);

    const latestDates = obj.positions.reduce((acc, total) => {
        const {equipmentId, date } = total;
        const recentDate = acc[equipmentId];

        if(!recentDate || new Date(date) > new Date(recentDate.date)) {
            acc[equipmentId] = {date, lat:total.lat, lon: total.lon};
        }
        return acc;
    }, {});

    console.log(latestDates)
        
    return(
    <Container>
    <section className='map-component'>
        <div className='map'>
        <MapContainer center={position} zoom={13} scrollWheelZoom={true}>
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={position} icon={customIcon}>
                <Popup className="custom-popup">
                    A pretty CSS3 popup. <br /> Easily customizable.
                </Popup>
            </Marker>
        </MapContainer>
        </div>
    </section>
    </Container>
    )
}

export default Map;
