import { useEffect } from "react";
import { useMap } from "react-leaflet";
import L from 'leaflet'

function MapLegend({ position }) {
  const map = useMap();

  useEffect(() => {
    const legend = L.control({ position: position });

    legend.onAdd = () => {
      const div = L.DomUtil.create("div", "info legend");
      const grades = ['Azul', 'Verde', 'Vermelho'];
      let labels = []
      let from;
      let to;
      
      for (let i = 0; i < grades.length; i++) {
        const color = i === 0 ? 'blue' : i === 1 ? 'green' : 'red';
        const label = '<i style="background:' + color + '"></i> ' + grades[i];
        labels.push(label);
      }
      div.innerHTML =
        "<h4>Equipamento</h4>" +
        labels.join('<br>') +
        "<p>Azul: Garra traçadeira</p>" + 
        "<p>Verde: Harverster</p>" +
        "<p>Vermelho: Caminhão de carga</p>" ;
      return div;
    };

    legend.addTo(map);

    return () => {
      map.removeControl(legend);
    };
  }, [position, map]);

  return null;
}

export default MapLegend;
