import React, { useState } from 'react';
import equipmentPositionHistory from '../../data/equipmentPositionHistory.json';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet';
import { Icon } from 'leaflet';
import { Container } from 'react-bootstrap';
import 'leaflet/dist/leaflet.css';

function Map() {
    const [positions, setPositions] = useState([]);
    const lastPositions = {};

    const teste = [51.505, -0.09]

    const customIcon = new Icon({
        iconUrl: '/marker.png',
        iconSize: [15, 25]
    })

    equipmentPositionHistory.forEach(equipment => {
        const lastPosition = equipment.positions.reduce((prev, current) => {
            return (new Date(prev.date) > new Date(current.date)) ? prev : current;
        });
        console.log(lastPosition)
        lastPositions[equipment.equipmentId] = lastPosition
    });

    
    console.log(positions)
        
    return(
    <Container>
    <section className='map-component'>
        <div className='map'>
        <MapContainer center={teste} zoom={13} scrollWheelZoom={true}>
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            /> 
            <Marker position={teste} icon={customIcon}>
                <Popup className="custom-popup">
                    A pretty CSS3 popup. <br /> Easily customizable.
                </Popup>
            </Marker>
        </MapContainer>
        </div>
    </section>
    <div>
    {Object.keys(lastPositions).map(equipmentId => {
      const position = lastPositions[equipmentId];
      return (
        <div key={equipmentId}>
          <p>Equipment ID: {equipmentId}</p>
          <p>Last position: {position.lat}, {position.lon}</p>
        </div>
      );
    })}
  </div>
    </Container>
    )
}

export default Map;
